import React, { useState } from "react";
import './NumberGame.css'
const genRandom = () => {
    return Math.floor(Math.random() * 10) + 1;
}

const NumberGame = (props) => {
    const [guess, setGuess] = useState(genRandom());
    const [target, setTarget] = useState(genRandom);
    const winner = guess === target;
  return (<div>
      <h1>Target Num: {target} </h1>
      <h1 className={winner? 'NumberGame-winner' : 'NumberGame-loser'}>Your Guess: {guess} </h1>
      {}
  </div>);
};

export default NumberGame;
